parameters.jsCode:
return [{
  json: {
    // Dữ liệu từ item 0 (persona)
    persona_output: items[0].json.persona_output,

    // Dữ liệu từ item 1 (research_output)
    outline: items[1].json.output.outline,
    tu_khoa_phu: items[1].json.output.tu_khoa_phu,

    // Dữ liệu từ item 2 (sheet metadata)
    chu_de: items[2].json["Người dùng nhập chủ đề"],
    tu_khoa_chinh: items[2].json["Từ khóa chính"],
    y_dinh_tim_kiem: items[2].json["Ý Định tìm kiếm của người dùng"]
  }
}];

// Gộp hai phần tử (bài viết & dòng Google Sheets) thành một object duy nhất
const post = items.find(i => i.json.link);
const sheet = items.find(i => i.json["Từ khóa chính"]);

return [
  {
    json: {
      tuKhoaChinh: sheet?.json["Từ khóa chính"] || '',
      linkBaiViet: post?.json.link || '',
      row_number: sheet?.json.row_number || '',
      trangThai: 'Done'
    }
  }
];

return items.map(item => {
  item.json.data = item.json.data.replace(/<h[1-6]\s+id="[^"]+"/g, match => {
    return match.replace(/ id="[^"]+"/, '');
  });
  return item;
});

// Chuẩn hóa prompt và tên ảnh từ object output
return [{
  json: {
    prompt_img: $json.output?.prompt_img?.toString().trim() ?? 'Generate a simple image of product',
    name_img: $json.output?.name_img ?? 'virion_image'
  }
}];
